import React,{useState} from 'react'

export default function TextForm(props) {
   
    const handleUpClick=()=>
    {
        // console.log("upper case was clicked" + text)
        let newText= text.toUpperCase();
        setText(newText);
        updateHistory(newText);
   }
    
   const handlelowerClick=()=>
    {
        let newText= text.toLowerCase();
        setText(newText);
        updateHistory(newText);
        
   }


   const handleonChange=(event)=>
   {
        setText(event.target.value);
        setText(newText);
        updateHistory(newText);
   }


   const ClearTEXT=()=>
    {
        let newText=" ";
        setText(newText)
    }
    
    const updateHistory=(newText)=>{
        const newhistory = history.slice(0,pointer+1);
        newhistory.push(newText)
        sethistory(newhistory);
        setpointer(newhistory.length-1);
    }

    const handleRedo = ()=>
        {
            if(pointer<history.length-1){
                setpointer(pointer+11);
                setText(history[pointer+1]);

            }
        }
        

    const handleUndo=()=>
        {
            if(pointer>0){
                setpointer(pointer-1);
                setText(history[pointer-1]);

            }
            
        }
        
            
   const countWords = (str) => {
    const trimmedStr = str.trim();
    if (!trimmedStr) {
        return 0;
    }
    return trimmedStr.split(/\s+/).length;
   }


    const [text,setText] = useState('Enter text here');
    // text="new text "    //wrongway to change state
    //setText("newtext");   //correct way to change state
    const [history,sethistory] =useState(['']);
    const [pointer,setpointer] =useState(0);


  return (
    <div className = "container">
        <h1>{props.heading}</h1>
        <div className="mb-3">
        {/* <label for="myBox" className="form-label">Example textarea</label> */}
        <textarea className="form-control"  value={text}  onChange={handleonChange}id="myBox" rows="10"></textarea> {/*on change is used for type*/}
        </div> 
        <div>
        <button className="btn btn-primary mx-1" onClick={handleUpClick}>Convert to Uppercase
        </button>
        <button className="btn btn-primary mx-1" onClick={handlelowerClick}>Convert to Lowercase
        </button>
        <button className="btn btn-primary mx-1" onClick={ClearTEXT}>Clear Text
        </button>
        <button className="btn btn-secondary" onClick={handleUndo} disabled={pointer === 0}>Undo</button>
        <button className="btn btn-secondary" onClick={handleRedo} disabled={pointer === history.length - 1}>Redo</button>
        </div>
        <div className="container my-3">
                <h1>Your Text Summary</h1>
                <p>{countWords(text)} words and {text.length} characters</p>
                <p>{0.008 * countWords(text)} Minutes read</p>
                <h2>Preview</h2>
                <p>{text}</p>
      </div>
    </div>
  )
}
